import { useState } from "react";
import axios from "axios";

const CreateProject = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [budget, setBudget] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = { title, description, budget, clientId: user._id };

    try {
      const res = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/projects/create`, payload);
      alert("Project Created!");
    } catch (err) {
      console.error("Error:", err.response?.data?.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" />
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
      <input value={budget} onChange={(e) => setBudget(e.target.value)} placeholder="Budget" />
      <button type="submit">Create Project</button>
    </form>
  );
};

export default CreateProject;
