  {
    name: "Amina Mwangi",
    title: "UI/UX Designer",
    rate: 30,
    image: "https://via.placeholder.com/400x300?text=Amina",
  },
  {
    name: "Daniel Otieno",
    title: "Full-Stack Developer",
    rate: 45,
