  return (
    <div className="min-h-screen bg-gradient-to-tr from-white via-blue-50 to-blue-100 p-6">
      {/* âœ¨ Hero Section */}
      <div className="text-center my-12 animate-fadeIn">
        <h1 className="text-4xl font-extrabold text-blue-700 mb-4">
          FreelancersKE â€” Your Gateway to Talent
        </h1>
        <p className="text-gray-700 mb-6 text-lg max-w-2xl mx-auto">
          Hire top-rated freelancers in Kenya or showcase your skills to get hired. Sign up and start building together!
        </p>
        <div className="flex justify-center gap-4">
          <Link
            to="/login"
            className="px-6 py-2 bg-blue-600 text-white rounded shadow hover:bg-blue-700 transition duration-300"
