        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {freelancers.map((freelancer, index) => (
            <FreelancerCard key={index} {...freelancer} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
