import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const ClientDashboard = () => {
  const [myProjects, setMyProjects] = useState([]);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const token = localStorage.getItem("token"); // Adjust to your auth setup
        const res = await axios.get(
          `${process.env.REACT_APP_BACKEND_URL}/projects/my-projects`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setMyProjects(res.data);
      } catch (err) {
        console.error("‚ùå Failed to load client projects:", err.message);
      }
    };

    fetchProjects();
  }, []);

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-4">Welcome, Client üßë‚Äçüíº</h1>
      <p className="text-gray-600 mb-6">
        Post new projects, manage freelancers, and review proposals here.
      </p>

      {/* üîß Quick Links */}
      <div className="flex gap-4 mb-6">
        <Link
          to="/create-project"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          ‚ûï Create Project
        </Link>
        <Link
          to="/my-projects"
          className="bg-gray-100 text-gray-800 px-4 py-2 rounded hover:bg-gray-200"
        >
          üìÅ View My Projects
        </Link>
      </div>

      {/* üóÇ Recent Projects Preview */}
      <div>
        <h2 className="text-xl font-semibold text-gray-700 mb-2">Latest Projects You Posted</h2>
        {myProjects.length > 0 ? (
          <ul className="space-y-3">
            {myProjects.slice(0, 3).map((project) => (
              <li
                key={project._id}
                className="p-4 bg-white rounded shadow border border-gray-100"
              >
                <h3 className="font-bold text-blue-600">{project.title}</h3>
                <p className="text-gray-600">{project.description}</p>
                <span className="text-sm text-gray-400">üí∞ Budget: {project.budget}</span>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-500">You haven‚Äôt posted any projects yet.</p>
        )}
      </div>
    </div>
  );
};

export default ClientDashboard;
